import React,{ useState } from 'react';
import { dartTheme, lightTheme, GlobalStyles} from './Themes'


// styles
import styled, { ThemeProvider} from 'styled-components';

const StyledApp = styled.div``;


const App = () => {
    const [theme , setTheme ] = useState("dark");

    const themeToggler = () =>{
        theme === 'light' ? setTheme('dark') : setTheme('light');
    };


    return (
        <ThemeProvider theme= { theme === 'light' ? dartTheme : lightTheme} >
            <GlobalStyles/>
            <StyledApp>
                hello
            </StyledApp>
        </ThemeProvider >
    )
}

export default App
