import styled from "styled-components";

export const Header = styled.div`
    padding: 8px 16px;
    box-shadow: 1px 0px 7px 3px #ede9e9;
`;



export const Wrapper = styled.div`
    position: relative;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: space-between;
    padding-top: .5rem;
    padding-bottom: .5rem;

    display: flex;
    justify-content: space-between;


`;

export const ContextRight = styled.div`

`;
export const ContextLeft  = styled.div`
    display: block;

    ul{
        list-style: none;
        display: flex;
        justify-content: flex-end;

        li{
            margin: 0px 8px;

            form{
                display: block;
                margin-top: 0em;
            }
        }

    }


    button{
        display: none;
    }

`;


export const Image = styled.img`
    width: 120px;
`;
