import React, { Component } from 'react';
import './App.css';
class App extends Component {

  state = {
    clients : [
      {id : "1", nom : "pricemou"},
      {id : "2", nom : "toure" },
      {id : "3", nom : "ib moto" },

    ],
    compteur : 0,
    nouveauClient :''
  }


  handleSubmit = (event) => {
    event.preventDefault(); 

    const id = new Date().getTime();
    const nom = this.state.nouveauClient;

    const client = {id , nom}
    const clients = this.state.clients.slice();
    clients.push(client)

    this.setState({clients})
    this.setState({nouveauClient : ""})

  }

  handleChange = (event)=>{
    const value = event.currentTarget.value;
    this.setState({nouveauClient: value})
  }

  handleAdd = () => {
    const clients = this.state.clients.slice();
    clients.push({id :"4", nom:"maman"})
    this.setState({compteur: this.state.compteur + 1})
    this.setState({clients : clients})
  }

  handeleDelet = (id) =>{
    const clients = this.state.clients.slice();
    const index = clients.findIndex((client)=>{
      console.log
      return client.id === id
    })
    clients.splice(index, 1)
    this.setState({clients:clients})
  }

  render(){
    const title = 'Liste des clients'
    return (
      <div className="">
        <div className="">
          <h2> {title} </h2> 
          {this.state.compteur}
          <button onClick={this.handleAdd} >confimer</button>
          <ul>
              {this.state.clients.map((client) => { 
               return <li> {client.nom} <button onClick={()=> this.handeleDelet(client.id)}>X</button></li>
             })}
          </ul>
          <form onSubmit={this.handleSubmit}>
             <input value={this.state.nouveauClient} onChange={this.handleChange} type="text"  placeholder="Ajouer un client" />
             <button>confimer</button>
          </form>

        </div>
      </div>
    )
  } 
}

export default App
