import React, { Component } from 'react'

class List extends Component{

    toggleTodo(todo){
      console.log("todo clicked",todo)
    }

    showTodos(todos){
      return(
        todos.map((todo) => {
          return(
            <div className="todo"  

              onClick={(todo) => this.toggleTodo(todo)} >
              {todo.title} {todo.dome ? 'oui' : 'non'}
           </div>
          )
        })
      )
    }


    render(){
      return(
        <div className="liste">
          Todos: [{this.props.todos.length}]
          { this.showTodos(this.props.todos)} 
          </div>
      );
    }
  }

  export default List;
