import styled from "styled-components";

export const Wrapper = styled.div`
    width: 100%;
    padding-bottom: 3rem;
    position: relative;

`;

export const Context = styled.div`
    display: flex;
    -webkit-box-pack: justify;
    justify-content: space-between;
    align-items: flex-end;

    a{
