import React, { useState } from 'react';
import './App.css';
import Client from './Client' 
import Clienform from './Clienform'
const App = () =>  {

  const [clients, setClients] = useState([
    {id : "1", nom : "pricemou"},
    {id : "2", nom : "toure" },
    {id : "3", nom : "ib moto" },
  ])

  const [compteur, setCompteur] = useState(0)

  const handleAD = (client) => {

    const updatedClients = clients.slice();
    updatedClients.push(clients)
    setClients(updatedClients )
  }

  const handleAdd = () => {
    const clients = clients.slice();
    clients.push({id :"4", nom:"maman"})
    setCompteur(compteur + 1)
    setClients(clients)
  }

  const handeleDelet = (id) =>{
    const updatedClients = clients.slice();
    const index = updatedClients.findIndex((client)=>{
      console.log(client.id)
      return client.id === id
    })
    updatedClients.splice(index, 1)
    setClients(updatedClients)
  }

    const title = 'Liste des clients'
    return (
      <div className="">
        <div className="">
          <h2> {title} </h2> 
          {compteur}
          <button onClick={handleAdd} >confimer</button>
          <ul>
              {clients.map((client) => { 
                return <Client detail={client} onDelete={handeleDelet} />
            })}

          </ul>
              <Clienform clienAd={handleAD} />
        </div>
      </div>
    )
}

export default App
