import { createGlobalStyle } from "styled-components";

export const GlobalStyles = createGlobalStyle`

    :root {
        --maxWidth: 1280px;
        --white : #fff;
        --lightGrey: #eee;
        --darkGrey: #2f2d2d;
        --medGrey: #1c1c1c;
        --fontSuperBig: 2.5rem;
        --fontBig: 1.5rem;
        --fontMed: 1.2rem;
        --fontSmall: 1rem;
    }

	body {
		background-color: ${(props) => props.theme.body};
	}

`;
