import React, { useState } from 'react'

const Clienform = (props) =>  {

    const [nouveauClient, setNouveauClient] = useState("")


    const handleChange = (event)=> {
        setNouveauClient(event.currentTarget.value)
      }

    const handleSubmit = (event) => {
        event.preventDefault(); 

        const id = new Date().getTime();
        const nom = nouveauClient;


        props.clienAd({id , nom});
        setNouveauClient("");
      }

        return (
            <form onSubmit={handleSubmit}>
             <input value={nouveauClient} onChange={handleChange} type="text"  placeholder="Ajouer un client" />
             <button>confimer</button>
          </form>
        )

}

export default Clienform
