import React, { Component } from 'react';
import './App.css';
import Client from "./Client"
class App extends Component {

  state = {
    client : [
      { id: 1, nom: "Pricemou Fromon"},
      { id: 2, nom: "Magali Pernin"},
      { id: 3, nom: "oseph Durand"},
    ],
    nouveauClient : "toure"
  };

  handelDele = (id) => {
    const clients = this.state.client.slice();
    const index = clients.findIndex((Clients) => {
      return Clients.id === id 
    });

    clients.splice(index, 1);
    this.setState({client: clients});
  }

  handleSubmit = (event) => { 
    event.preventDefault();

    const id = new Date().getTime();
    const nom = this.state.nouveauClient;
    const client = {id :id , nom: nom};

    const clients = [...this.state.client];
    clients.push(client)

    this.setState({client: clients, nouveauClient:''})
  }

  handleChange = (event) => {
    const value = event.currentTarget.value;
    this.setState({nouveauClient: value})
  }

  render(){

      const title = "Liste des Clients"
      const elements = this.state.client.map((client) =><li>{client.nom} <button onClick={()=> this.handelDele(client.id)} >X</button></li>)

    return (
      <div className="">
        <div className="">
          <h2>{title}</h2>
           <Client />
           <form onSubmit={this.handleSubmit}>
              <input value={this.state.nouveauClient} onChange={this.handleChange} type="text" placeholder="Ajoute un client" />
              <button >Confirmer</button>
           </form> 
        </div>
      </div>
    )
  } 
}

export default App
