      {id : "3", nom : "ib moto" },

    ]
  }

  render(){

    const title = 'Liste des clients'

    return (
      <div className="">
        <div className="">
          <h2> {title} </h2> 
          <ul>
              {this.state.clients.map((client) => { 
               return <li> {client.nom} <button>X</button></li>
             })}
          </ul>
          <form>
             <input type="text" placeholder="Ajouer un client" />
             <button>confimer</button>
          </form>

        </div>
      </div>
    )
  } 
}

export default App
