import React, { Component } from 'react';
import './App.css';
class App extends Component {

  state = {
    clients : [
      { id: 1, nom: "Pricemou Fromon"},
      { id: 2, nom: "Magali Pernin"},
      { id: 3, nom: "oseph Durand"},
    ],
    compteur : 0,
  };

  handleClick = () => {
    this.setState({ compteur: this.state.compteur++ })
    console.log(this.state)
  }

  render() {
      const title = "Liste des Clients"
      const element = <li>Pricemou Fromon</li>
      const elements = this.state.clients.map((client)=><li>{client.nom} <button>X</button></li>)

    return (
      <div className="">
        <div className="">
          <h2>{title}</h2>
          <button onClick={this.handleClick}>Click me</button>
          {this.state.compteur}
           <ul>
             {elements}
           </ul>
           <form>
              <input type="text" placeholder="Ajoute un client" />
              <button>Confirmer</button>
           </form>
        </div>
      </div>
    )
  } 
}

export default App
