import React, { Component } from 'react';
import './App.css';
import Client from './Client' 
import Clienform from './Clienform'
class App extends Component {

  state = {
    clients : [
      {id : "1", nom : "pricemou"},
      {id : "2", nom : "toure" },
      {id : "3", nom : "ib moto" },

    ],
    compteur : 0,
    nouveauClient :''
  }


  handleAD = (event) => {

    const clients = this.state.clients.slice();
    clients.push(client)

    this.setState({clients})
    this.setState({nouveauClient : ""})

  }



  handleAdd = () => {
    const clients = this.state.clients.slice();
    clients.push({id :"4", nom:"maman"})
    this.setState({compteur: this.state.compteur + 1})
    this.setState({clients : clients})
  }

  handeleDelet = (id) =>{
    const clients = this.state.clients.slice();
    const index = clients.findIndex((client)=>{
      console.log(client.id)
      return client.id === id
    })
    clients.splice(index, 1)
    this.setState({clients:clients})
  }

  render(){
    const title = 'Liste des clients'
    return (
      <div className="">
        <div className="">
          <h2> {title} </h2> 
          {this.state.compteur}
          <button onClick={this.handleAdd} >confimer</button>
          <ul>
              {this.state.clients.map((client) => { 
                return <Client detail={client} onDelete={this.handeleDelet} />
            })}

          </ul>
              <Clienform/>
        </div>
      </div>
    )
  } 
}

export default App
