import './App.css';
class App extends Component {

  state = {
    clients : [
      {id : "1", nom : "pricemou"},
      {id : "2", nom : "toure" },
      {id : "3", nom : "ib moto" },

    ]
  }

  render(){

    const title = 'Liste des clients'
    const element = [<ul>
      <li>Pricemou fromon IB <button>X</button> </li>
      <li>Pricemou fromon claude <button>X</button> </li>
    </ul>
    ]

    const elements = this.state.clients.map((client) => {
       return <li> {client.nom} </li>
    } )

    return (
      <div className="">
        <div className="">
          <h2> {title} </h2> 
          <ul>
              {elements}
          </ul>
          <form>
             <input type="text" placeholder="Ajouer un client" />
             <button>confimer</button>
          </form>

        </div>
      </div>
    )
  } 
}

export default App
