import React, { Component } from 'react'

class Clienform extends Component {

    state = {
        nouveauClient: ""
    }


    handleChange = (event)=> {
        const value = event.currentTarget.value;
        this.setState({nouveauClient: value})
      }

    handleSubmit = (event) => {
        event.preventDefault(); 

        const id = new Date().getTime();
        const nom = this.state.nouveauClient;

        const client = {id , nom}
        this.props.clienAd(client)
        this.setState({nouveauClient : ""})
      }


    render() {
        return (
            <form onSubmit={this.handleSubmit}>
             <input value={this.state.nouveauClient} onChange={this.handleChange} type="text"  placeholder="Ajouer un client" />
             <button>confimer</button>
          </form>
        )
    }
}

export default Clienform
